const qts = require('./abthoughts.json');
const colors = require('colors');
let getQoutes = (count) => {
    let result = [];
    for(let i  = 0; i < count; i++){
        result.push(qts.AB_THOUGHTS[i]);
    }
  return result;
}
let log = {};
log.success = (data) => { console.log(colors.green(data)); }
log.error   = (data) => {console.log(colors.red(data)); }
log.info = (data) => { console.log(colors.bgGreen(data)); }
log.important = (data) => { console.log(colors.bgGray.underline(data)); }
log.array = (data) => { console.log(colors.rainbow(data.toString())); }


 module.exports = log;